contracts/DytallixBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title DytallixBridge
 * @dev Cross-chain bridge contract for Dytallix ecosystem
 * Supports asset locking/unlocking and cross-chain transfers
 */
contract DytallixBridge is 
    Initializable,
    PausableUpgradeable, 
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable 
{
    using SafeERC20 for IERC20;

    // Roles
    bytes32 public constant VALIDATOR_ROLE = keccak256("VALIDATOR_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // Bridge configuration
    uint256 public validatorThreshold;
    uint256 public bridgeFeeBps; // Fee in basis points (100 = 1%)
    uint256 public nonce;

    // Asset management
    mapping(address => bool) public supportedAssets;
    mapping(address => uint256) public lockedBalances;
    mapping(bytes32 => bool) public processedTransactions;

    // Cross-chain transactions
    struct BridgeTransaction {
        address asset;
        uint256 amount;
        address recipient;
        string destinationChain;
        uint256 nonce;
        bool processed;
    }

    mapping(bytes32 => BridgeTransaction) public bridgeTransactions;
    mapping(bytes32 => uint256) public validatorSignatures;

    // Events
    event AssetLocked(
        address indexed asset,
        address indexed sender,
        uint256 amount,
        string destinationChain,
        address recipient,
        uint256 nonce
    );

    event AssetUnlocked(
        address indexed asset,
        address indexed recipient,
        uint256 amount,
        bytes32 indexed transactionId
    );

    event AssetAdded(address indexed asset);
    event AssetRemoved(address indexed asset);
    event ValidatorAdded(address indexed validator);
    event ValidatorRemoved(address indexed validator);
    event BridgeConfigUpdated(uint256 threshold, uint256 feeBps);

    modifier onlyValidator() {
        require(hasRole(VALIDATOR_ROLE, msg.sender), "Not a validator");
        _;
    }

    modifier onlyOperator() {
        require(hasRole(OPERATOR_ROLE, msg.sender), "Not an operator");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address admin,
        uint256 _validatorThreshold,
        uint256 _bridgeFeeBps
    ) public initializer {
        __Pausable_init();
        __AccessControl_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(OPERATOR_ROLE, admin);
        _grantRole(PAUSER_ROLE, admin);

        validatorThreshold = _validatorThreshold;
        bridgeFeeBps = _bridgeFeeBps;
        nonce = 1;
    }

    /**
     * @dev Lock assets for cross-chain transfer
     */
    function lockAsset(
        address asset,
        uint256 amount,
        string calldata destinationChain,
        address recipient
    ) external nonReentrant whenNotPaused {
        require(supportedAssets[asset], "Asset not supported");
        require(amount > 0, "Amount must be positive");
        require(recipient != address(0), "Invalid recipient");

        // Calculate fee
        uint256 fee = (amount * bridgeFeeBps) / 10000;
        uint256 netAmount = amount - fee;

        // Transfer tokens to bridge
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);

        // Update locked balance
        lockedBalances[asset] += netAmount;

        // Increment nonce
        uint256 currentNonce = nonce++;

        emit AssetLocked(
            asset,
            msg.sender,
            netAmount,
            destinationChain,
            recipient,
            currentNonce
        );
    }

    /**
     * @dev Unlock assets after cross-chain transfer validation
     */
    function unlockAsset(
        bytes32 transactionId,
        address asset,
        address recipient,
        uint256 amount,
        bytes[] calldata signatures
    ) external onlyValidator nonReentrant whenNotPaused {
        require(!processedTransactions[transactionId], "Transaction already processed");
        require(supportedAssets[asset], "Asset not supported");
        require(signatures.length >= validatorThreshold, "Insufficient signatures");

        // Verify signatures (simplified - in production would verify actual signatures)
        require(_verifySignatures(transactionId, signatures), "Invalid signatures");

        // Mark as processed
        processedTransactions[transactionId] = true;

        // Update locked balance
        require(lockedBalances[asset] >= amount, "Insufficient locked balance");
        lockedBalances[asset] -= amount;

        // Transfer tokens to recipient
        IERC20(asset).safeTransfer(recipient, amount);

        emit AssetUnlocked(asset, recipient, amount, transactionId);
    }

    /**
     * @dev Add supported asset
     */
    function addSupportedAsset(address asset) external onlyOperator {
        require(asset != address(0), "Invalid asset address");
        supportedAssets[asset] = true;
        emit AssetAdded(asset);
    }

    /**
     * @dev Remove supported asset
     */
    function removeSupportedAsset(address asset) external onlyOperator {
        supportedAssets[asset] = false;
        emit AssetRemoved(asset);
    }

    /**
     * @dev Add validator
     */
    function addValidator(address validator) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(VALIDATOR_ROLE, validator);
        emit ValidatorAdded(validator);
    }

    /**
     * @dev Remove validator
     */
    function removeValidator(address validator) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(VALIDATOR_ROLE, validator);
        emit ValidatorRemoved(validator);
    }

    /**
     * @dev Update bridge configuration
     */
    function updateBridgeConfig(
        uint256 _validatorThreshold,
        uint256 _bridgeFeeBps
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_validatorThreshold > 0, "Invalid threshold");
        require(_bridgeFeeBps <= 1000, "Fee too high"); // Max 10%

        validatorThreshold = _validatorThreshold;
        bridgeFeeBps = _bridgeFeeBps;

        emit BridgeConfigUpdated(_validatorThreshold, _bridgeFeeBps);
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause
     */
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @dev Verify validator signatures (simplified implementation)
     */
    function _verifySignatures(
        bytes32 transactionId,
        bytes[] calldata signatures
    ) internal view returns (bool) {
        // Simplified verification - in production would verify actual ECDSA signatures
        // For now, just check if we have enough signatures from validators
        uint256 validSignatures = 0;

        for (uint256 i = 0; i < signatures.length; i++) {
            if (signatures[i].length == 65) { // Standard ECDSA signature length
                validSignatures++;
            }
        }

        return validSignatures >= validatorThreshold;
    }

    /**
     * @dev Get locked balance for asset
     */
    function getLockedBalance(address asset) external view returns (uint256) {
        return lockedBalances[asset];
    }

    /**
     * @dev Check if transaction is processed
     */
    function isTransactionProcessed(bytes32 transactionId) external view returns (bool) {
        return processedTransactions[transactionId];
    }

    /**
     * @dev Get bridge configuration
     */
    function getBridgeConfig() external view returns (uint256, uint256, uint256) {
        return (validatorThreshold, bridgeFeeBps, nonce);
    }
}

contracts/WrappedDytallix.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title WrappedDytallix
 * @dev Wrapped DYT token for Ethereum network
 * Represents DYT tokens locked on the Dytallix chain
 */
contract WrappedDytallix is ERC20, ERC20Burnable, AccessControl, Pausable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // Bridge contract address
    address public bridge;

    // Original chain information
    string public originalChain;
    address public originalAsset;

    event BridgeUpdated(address indexed oldBridge, address indexed newBridge);
    event TokensMinted(address indexed to, uint256 amount, bytes32 indexed txHash);
    event TokensBurned(address indexed from, uint256 amount, string destinationChain);

    constructor(
        string memory name,
        string memory symbol,
        address admin,
        address _bridge,
        string memory _originalChain,
        address _originalAsset
    ) ERC20(name, symbol) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(MINTER_ROLE, _bridge);
        _grantRole(PAUSER_ROLE, admin);

        bridge = _bridge;
        originalChain = _originalChain;
        originalAsset = _originalAsset;
    }

    /**
     * @dev Mint wrapped tokens when assets are locked on origin chain
     */
    function mint(
        address to, 
        uint256 amount, 
        bytes32 txHash
    ) external onlyRole(MINTER_ROLE) whenNotPaused {
        require(to != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be positive");

        _mint(to, amount);
        emit TokensMinted(to, amount, txHash);
    }

    /**
     * @dev Burn wrapped tokens for cross-chain transfer back to origin
     */
    function burnForBridge(
        uint256 amount,
        string calldata destinationChain
    ) external whenNotPaused {
        require(amount > 0, "Amount must be positive");
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");

        _burn(msg.sender, amount);
        emit TokensBurned(msg.sender, amount, destinationChain);
    }

    /**
     * @dev Update bridge contract (admin only)
     */
    function updateBridge(address newBridge) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newBridge != address(0), "Invalid bridge address");

        address oldBridge = bridge;

        // Revoke minter role from old bridge
        if (oldBridge != address(0)) {
            _revokeRole(MINTER_ROLE, oldBridge);
        }

        // Grant minter role to new bridge
        _grantRole(MINTER_ROLE, newBridge);
        bridge = newBridge;

        emit BridgeUpdated(oldBridge, newBridge);
    }

    /**
     * @dev Pause token transfers
     */
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * @dev Override transfer to include pause functionality
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
    }

    /**
     * @dev Get token information
     */
    function getTokenInfo() external view returns (
        string memory,
        address,
        address,
        uint256
    ) {
        return (originalChain, originalAsset, bridge, totalSupply());
    }
}

contracts/WrappedTokenFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./WrappedDytallix.sol";

/**
 * @title WrappedTokenFactory
 * @dev Factory contract for deploying wrapped tokens
 */
contract WrappedTokenFactory is Ownable {
    // Mapping of asset ID to wrapped token address
    mapping(string => address) public wrappedTokens;

    // Array of all deployed wrapped tokens
    address[] public allWrappedTokens;

    // Bridge contract address
    address public bridge;

    event WrappedTokenCreated(
        string indexed assetId,
        address indexed tokenAddress,
        string name,
        string symbol,
        string originalChain,
        address originalAsset
    );

    event BridgeUpdated(address indexed oldBridge, address indexed newBridge);

    constructor(address _bridge) {
        bridge = _bridge;
    }

    /**
     * @dev Create a new wrapped token
     */
    function createWrappedToken(
        string calldata assetId,
        string calldata name,
        string calldata symbol,
        string calldata originalChain,
        address originalAsset
    ) external onlyOwner returns (address) {
        require(wrappedTokens[assetId] == address(0), "Token already exists");
        require(bytes(assetId).length > 0, "Invalid asset ID");
        require(bytes(name).length > 0, "Invalid name");
        require(bytes(symbol).length > 0, "Invalid symbol");

        // Deploy new wrapped token
        WrappedDytallix wrappedToken = new WrappedDytallix(
            name,
            symbol,
            owner(),
            bridge,
            originalChain,
            originalAsset
        );

        address tokenAddress = address(wrappedToken);

        // Store mapping
        wrappedTokens[assetId] = tokenAddress;
        allWrappedTokens.push(tokenAddress);

        emit WrappedTokenCreated(
            assetId,
            tokenAddress,
            name,
            symbol,
            originalChain,
            originalAsset
        );

        return tokenAddress;
    }

    /**
     * @dev Update bridge contract for all wrapped tokens
     */
    function updateBridge(address newBridge) external onlyOwner {
        require(newBridge != address(0), "Invalid bridge address");

        address oldBridge = bridge;
        bridge = newBridge;

        // Update bridge for all wrapped tokens
        for (uint256 i = 0; i < allWrappedTokens.length; i++) {
            WrappedDytallix(allWrappedTokens[i]).updateBridge(newBridge);
        }

        emit BridgeUpdated(oldBridge, newBridge);
    }

    /**
     * @dev Get wrapped token address by asset ID
     */
    function getWrappedToken(string calldata assetId) external view returns (address) {
        return wrappedTokens[assetId];
    }

    /**
     * @dev Get all wrapped token addresses
     */
    function getAllWrappedTokens() external view returns (address[] memory) {
        return allWrappedTokens;
    }

    /**
     * @dev Get number of deployed wrapped tokens
     */
    function getWrappedTokenCount() external view returns (uint256) {
        return allWrappedTokens.length;
    }
}

